/**
 * ## Description
 * $SynchDataFunctions
 * Synch your data with [JSONBIN.io](https://jsonbin.io)
 * ### Release Notes
 * * v1.0.8
 *   * Don't synch trashed tiddlers
 */
/**
 * ## Data
 * ```json
 * {
 *   "version": 1.0.7
 * }
 * ```
 */
// ## Code
// ```javascript
// TODO: Push force, clearing remote
// TODO: Pull force, clearing local
// TODO: Selective Synch: Include/Exclude Tags/Packages
tw.macros.synch = (function(){
  // TODO: Make events handler support asynch
  // tw.events.subscribe('synch.full')
  // onclick=tw.events.send(\'synch.full\')
  return {
    // <<synch.full>>: Push/pull to/from remote
    full() {
      return tw.ui.button('{{$IconSynch}}', 'synch.full', null, 'btn-synch', 'title="Synch Data"');
    },
    async doFull() {
      return await synch({pull: true, push: true});
    },
    // <<synch.test>>: Simulate push/pull to/from remote
    test() {
      return '<button onclick="tw.macros.synch.doTest()">Synch Test</button>';
    },
    async doTest() {
      return await synch({pull: true, push: true, dryRun: true});
    },
    // <<synch.pull>>: Only import from remote
    pull() {
      return tw.ui.button('{{$IconPull}}', 'synch.pull', null, 'btn-synch-pull', 'title="Pull Synched Data"');
    },
    async doPull() {
      return await synch({pull: true, push: false});
    },
    // <<synch.push>>: Only write to remote
    push() {
      return tw.ui.button('{{$IconPush}}', 'synch.push', null, 'btn-synch-push', 'title="Push Synched Data"');
    },
    async doPush() {
      return await synch({pull: false, push: true});
    },
    // TODO: Delete all remote and push (backup)
    // TODO: Delete all local and pull (restore)
  };

  // eslint-disable-next-line complexity
  async function synch({push = true, pull = true, dryRun = false}) {

    if (!push && !pull) throw new Error('SynchDataFunctions: Please supply push or pull parameters!');

    let settings = tw.call('getJSONObject', '$GeneralSettings');
    if (!settings || !settings.synch?.JSONBin?.accessKey || !settings.synch?.JSONBin?.binId) return tw.ui.notify('No JSONBin accessKey/binId found in $GeneralSettings!', 'W');
    let headers = {'X-Access-Key': settings.synch.JSONBin.accessKey};

    // Fetch remote
    let res = await fetch('https://api.jsonbin.io/v3/b/' + settings.synch.JSONBin.binId, {headers});
    if (!res.ok) return tw.ui.notify(`Restore failed '${res.status}' (see log)`, 'E');
    let result = await res.json();
    let remoteTiddlers = result.record.tiddlers || [];
    let remoteTrashedTiddlers = result.record.trashed || [];

    let log = [];
    let remote = {create: [], update: [], delete: []};
    let local = {create: [], update: [], delete: []};

    let localTiddlers = tw.tiddlers.all
      .filter(t => !t.isRawShadow); // Don't synch raw shadows - See BUG below

    // eslint-disable-next-line complexity
    remoteTiddlers.forEach(remoteTiddler => {
      remoteTiddler.created = new Date(remoteTiddler.created);
      remoteTiddler.updated = new Date(remoteTiddler.updated);
      let localTiddler = localTiddlers.find(t => t.title === remoteTiddler.title);
      if (remoteTiddler.tags.includes('$NoSynch') || localTiddler?.tags.includes('$NoSynch')) log.push(`Skipping $NoSynch tiddler [[${localTiddler.title}]]`);
      // if (remoteTiddler.title.match(/SynchLog/)) debugger;
      // TODO: BUG: Deleted local shadow tiddler is pulled in from remote
      let deletedLocalTiddler = tw.tiddlers.trashed.find(t => t.title === remoteTiddler.title);
      let deletedLocally = !localTiddler && deletedLocalTiddler?.updated > remoteTiddler.updated;
      let createdRemotely = !localTiddler && !deletedLocally;
      let updatedLocally = localTiddler?.updated > remoteTiddler.updated;
      let updatedRemotely = remoteTiddler.updated > localTiddler?.updated;
      if (deletedLocally) {
        // Delete Remote
        if (push) remote.delete.push(deletedLocalTiddler.title);
      } else if (createdRemotely) {
        // Restore Local: Updated remotely after local delete
        if (pull) {
          if (!dryRun) tw.run.addTiddler(remoteTiddler);
          local.create.push(remoteTiddler.title);
          log.push(`Created local tiddler [[${remoteTiddler.title}]]`);
        }
      } else if (updatedLocally) {
        // Local update is newer
        if (push) remote.update.push(localTiddler.title); // üëà
      } else if (updatedRemotely) {
        // Remote update is newer
        if (push) {
          if (!dryRun) tw.run.updateTiddlerHard(remoteTiddler.title, remoteTiddler);
          local.update.push(remoteTiddler.title);
          log.push(`Updated local tiddler [[${localTiddler.title}]]`); // üëà
        }
      }
    });

    localTiddlers.forEach(localTiddler => {
      if (localTiddler.tags.includes('$NoSynch')) log.push(`Skipping $NoSynch tiddler [[${localTiddler.title}]]`);
      let remoteTiddler = remoteTiddlers.find(t => t.title === localTiddler.title);
      if (remoteTiddler) {
        remoteTiddler.created = new Date(remoteTiddler.created);
        remoteTiddler.updated = new Date(remoteTiddler.updated);
      }
      let deletedRemoteTiddler = remoteTrashedTiddlers.find(t => t.title === localTiddler.title);
      let deletedRemotely = !remoteTiddler && deletedRemoteTiddler?.updated > localTiddler.updated;
      let createdLocally = !remoteTiddler && !deletedRemotely;
      if (deletedRemotely) {
        // Delete Locally
        if (pull) {
          if (!dryRun) tw.run.deleteTiddler(localTiddler.title, true);
          local.delete.push(remoteTiddler.title);
          log.push(`Deleted local tiddler [[${localTiddler.title}]]`);
        }
      } else if (createdLocally) {
        if (push) remote.create.push(localTiddler.title);
      }
      // Updated locally/remotely handled above ‚òùÔ∏è
    });

    if (remote.create.length + remote.update.length + remote.delete.length +
      local.create.length + local.update.length + local.delete.length === 0
    ) return tw.ui.notify('No changes to synch', 'S');

    if (push) {
      // Perform remote updates
      remote.create.forEach(title => {
        log.push(`Created remote tiddler [[${title}]]`);
      });
      remote.update.forEach(title => {
        log.push(`Updated remote tiddler [[${title}]]`);
      });
      remote.delete.forEach(title => {
        log.push(`Deleted remote tiddler [[${title}]]`);
      });
    }

    let logTiddler = {
      title: `$SynchLog ${new Date().toISOString()}`,
      text: logSummary(local, remote) + '\n\n## Log\n' + log.join('  \n') + '',
      tags: ['$SynchLog'],
      type: 'x-twikki',
    };

    if (push && !dryRun) {
      let body = JSON.stringify({
        tiddlers: localTiddlers,
        visible: tw.tiddlers.visible,
      });
      // We never push/pull trashed as this is local information (we read but never write it)
      res = await fetch('https://api.jsonbin.io/v3/b/' + settings.synch.JSONBin.binId, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'X-Access-Key': settings.synch.JSONBin.accessKey,
        },
        body,
      });
      // doesn't get caught by notify!! throw new Error('Backup failed: ' + res.statusText);
      if (!res.ok) return tw.ui.notify(`Synch (push) to remote failed '${res.status}' (see log)`, 'E');
    }

    if (!dryRun) tw.events.send('reboot.softer');

    if (push && pull) {
      tw.ui.notify('Synch (full) complete!', 'S');
    } else if (pull) {
      tw.ui.notify('Synch (pull) complete!', 'S');
    } else if (push) {
      tw.ui.notify('Synch (push) complete!', 'S');
    }

    tw.run.previewTiddler(logTiddler);

    function logSummary(local, remote) {
      return `
## Local
* Created (${local.create.length}) ${local.create.length ? ':\n  * [[' + local.create.join(']]\n  * [[') + ']]\n' : ''}
* Updated (${local.update.length}) ${local.update.length ? ':\n  * [[' + local.update.join(']]\n  * [[') + ']]\n' : ''}
* Deleted (${local.delete.length}) ${local.delete.length ? ':\n  * [[' + local.delete.join(']]\n  * [[') + ']]\n' : ''}
## Remote
* Created (${remote.create.length}) ${remote.create.length ? ':\n  * [[' + remote.create.join(']]\n  * [[') + ']]\n' : ''}
* Updated (${remote.update.length}) ${remote.update.length ? ':\n  * [[' + remote.update.join(']]\n  * [[') + ']]\n' : ''}
* Deleted (${remote.delete.length}) ${remote.delete.length ? ':\n  * [[' + remote.delete.join(']]\n  * [[') + ']]\n' : ''}
`.trim();
    }
  }

})();
tw.events.subscribe('synch.full', 'tw.macros.synch.doFull');
tw.events.subscribe('synch.push', 'tw.macros.synch.doPush');
tw.events.subscribe('synch.pull', 'tw.macros.synch.doPull');

(function(){

  tw.macros.core.TrashCanIcon = () => {
    let count = tw.tiddlers.trashed.length;
    return tw.ui.button('{{$IconDelete}}', 'tiddler.show', '$TrashManager', 'trashCanIcon', `title="${count} trashed tiddlers"`);
  // return `<a id="trashCanIcon" href="#$TrashManager">üóëÔ∏è (${count})</a>`;
  };
  tw.macros.core.TrashEmptyButton = () => {
    return tw.ui.button('Empty Trash', 'tiddlers.trashed.empty', null, 'trashButton');
  };
  tw.macros.core.TrashCanStatus = () => {
    return `<span id="trashStatus">${tw.tiddlers.trashed.length} tiddlers in the [trash can](#$TrashedTiddlers)!`;
  };
  tw.events.subscribe('tiddlers.trashed.empty', emptyTrash);
  function emptyTrash() {
    if (!confirm('Are you sure you want to permanently deleted your trashed tiddlers?')) return;
    tw.tiddlers.trashed = [];
    trashCanRefresh();
    tw.run.save();
  };
  tw.macros.core.TrashCanContents = () => {
    const list = tw.tiddlers.trashed.map(t => (`<li><a data-msg="tiddlers.trashed.preview" data-param="${t.title}">${t.title}</a> 
      <a title="Restore Tiddler" data-msg="tiddler.trashed.restore" data-param="${t.title}">üöÆ</a></li>`)).join('\n');
    return `<ul id="trashContents">${list}</ul>`;
  };
  tw.events.subscribe('tiddlers.trashed.preview', trashPreview);
  function trashPreview(title) {
    let tiddler = tw.tiddlers.trashed.find(t => t.title === title);
    tw.run.previewTiddler(tiddler, tw.templates.TiddlerTrashed);
  };

  tw.events.subscribe('tiddler.trashed.destroy', removeTiddlerFromTrash);
  function removeTiddlerFromTrash(title) {
    if (!confirm('Are you sure you want to permanently delete your trashed tiddler?')) return;
    let tiddlerIndex = tw.tiddlers.trashed.findIndex(t => t.title === title);
    tw.tiddlers.trashed.splice(tiddlerIndex, 1);
    trashCanRefresh();
    tw.core.dom.preview.close();
    tw.run.save();
  };

  tw.events.subscribe('tiddler.deleted', trashCanRefresh);
  tw.events.subscribe('trash.refresh', trashCanRefresh);
  function trashCanRefresh() {
    if (tw.core.dom.$('trashContents')) tw.core.dom.$('trashContents').outerHTML = tw.lib.markdown(tw.macros.core.TrashCanContents());
    if (tw.core.dom.$('trashCanIcon')) tw.core.dom.$('trashCanIcon').innerHTML = tw.macros.core.TrashCanIcon();
    if (tw.core.dom.$('trashStatus')) tw.core.dom.$('trashStatus').outerHTML = tw.lib.markdown(tw.macros.core.TrashCanStatus());
    tw.run.rerenderTiddler('$TrashedTiddlers');
  };

  tw.events.subscribe('tiddler.trashed.restore', restoreTiddlerFromTrash);
  function restoreTiddlerFromTrash(title) {
    if (!confirm('Are you sure you want to restore your trashed tiddler? This may overwrite an existing tiddler!')) return;
    let tiddler = tw.tiddlers.trashed.find(t => t.title === title);
    tw.run.addTiddler(tiddler);
    tw.run.reload();
    // So styles, search list etc update
    let tiddlerIndex = tw.tiddlers.trashed.findIndex(t => t.title === title);
    tw.tiddlers.trashed.splice(tiddlerIndex, 1);
    trashCanRefresh();
    tw.run.save();
  };
})();

tw.macros.core.WorkspaceSelect = () => {
  let workspace = tw.storage.get('workspace');
  return `<select id="workspace-select" onchange="tw.events.send('workspace.load.prompt', this.value);">
    <option value=""> - new workspace -</option>
    ${tw.storage.get('workspaces').map(n => `<option value="${n}"${n === workspace ? ' selected' : ''}>${n}</option>`).join('\n')}
  </select>`;
};
tw.macros.core.WorkspaceCreate = () => {
  return tw.ui.button('Create Workspace', 'workspace.create.prompt');
};
if (!tw.tmp.workspaceEvents) {
  tw.tmp.workspaceEvents = 1;
  tw.events.subscribe('workspace.load.prompt', (workspace) => {
    if (!workspace) workspace = tw.events.send('workspace.create.prompt')[0];
    if (!workspace) return;
    tw.events.send('workspace.load', workspace);
  }, 'WorkspaceWidgets');
  tw.events.subscribe('workspace.create.prompt', () => {
    let workspace = prompt('Enter name for new workspace:');
    if (!workspace) return;
    if (confirm('Would you like to clone this workspace?'))
      tw.events.send('workspace.clone', workspace);
    else
      tw.events.send('workspace.create', workspace);
    tw.core.dom.$('workspace-select').innerHTML = tw.macros.core.WorkspaceSelect();
    // tw.events.subscribe('tiddler.refresh', '$Workspaces'); // TODO: Dynamically get currentTiddler above?
    return workspace;
  }, 'WorkspaceWidgets');
}

// https://www.jsdelivr.com/package/npm/highlight.js
// https://cdnjs.com/libraries/highlight.js

(function(){

  tw.events.subscribe('ui.loaded', () => {
    tw.core.dom.addStyleSheet('highlight-light', 'https://cdnjs.cloudflare.com/ajax/libs/highlight.js/11.10.0/styles/atom-one-light.min.css');
    tw.core.dom.addStyleSheet('highlight-dark', 'https://cdnjs.cloudflare.com/ajax/libs/highlight.js/11.10.0/styles/atom-one-dark.min.css');
    tw.core.dom.addScript('highlight-core', 'https://cdnjs.cloudflare.com/ajax/libs/highlight.js/11.10.0/highlight.min.js');
    tw.events.subscribe('script.loaded', (name) => {
      if (name === 'highlight-core') {
        tw.core.dom.addScript('highlight-lang-javascript', 'https://cdnjs.cloudflare.com/ajax/libs/highlight.js/11.10.0/languages/javascript.min.js');
        tw.core.dom.addScript('highlight-lang-css', 'https://cdnjs.cloudflare.com/ajax/libs/highlight.js/11.10.0/languages/css.min.js');
        tw.core.dom.addScript('highlight-lang-xml', 'https://cdnjs.cloudflare.com/ajax/libs/highlight.js/11.10.0/languages/xml.min.js');
        tw.core.dom.addScript('highlight-lang-json', 'https://cdnjs.cloudflare.com/ajax/libs/highlight.js/11.10.0/languages/json.min.js');
        return;
      }
      if (name !== 'highlight-lang-json') return;
      tw.lib.highlight = window.hljs;
      tw.events.send('highlight.loaded');
    }, 'HighlightPlugin');
  }, 'HighlightPlugin');
  tw.events.subscribe('tiddler.rendered', ({tiddler, newElement}) => {
    newElement.querySelectorAll('pre code').forEach(el => (tw.lib.highlight?.highlightElement(el, {language: languageFromTiddlerType(tiddler.type)})));
  }, 'HighlightPlugin');

  function languageFromTiddlerType(type) {
    switch (type) {
      case 'script/js':
        return 'javascript';
      case 'css':
        return 'css';
      case 'html/template':
        return 'xml';
      case 'json':
        return 'json';
      default:
        return '';
    }
  }
})();
